import React, { useState, useEffect, useRef } from 'react';
import { Document, Page } from 'react-pdf';
import { motion, AnimatePresence } from 'framer-motion';
import PDFToolbar from './PDFToolbar';
import toast from 'react-hot-toast';

const SignatureHoverPreview = ({ signature, position, scale, visible }) => {
  if (!signature || !visible) return null;
  return (
    <motion.img
      src={signature.data}
      alt="Signature preview"
      className="pointer-events-none absolute z-50 opacity-50"
      style={{
        left: position.x - (signature.width || 90) * scale / 2,
        top: position.y - (signature.height || 30) * scale / 2,
        width: (signature.width || 180) * scale,
        height: (signature.height || 60) * scale,
        filter: 'drop-shadow(0 1px 2px rgba(0,0,0,0.08))',
        transition: 'left 0.08s, top 0.08s',
        pointerEvents: 'none',
      }}
      initial={{ opacity: 0, scale: 0.9 }}
      animate={{ opacity: 0.5, scale: 1 }}
      exit={{ opacity: 0, scale: 0.9 }}
    />
  );
};

const PlacedSignature = ({ field, scale }) => (
  <img
    src={field.signature?.data}
    alt="Placed signature"
    className="absolute z-30"
    style={{
      left: field.x * scale,
      top: field.y * scale,
      width: (field.width || 180) * scale,
      height: (field.height || 60) * scale,
      pointerEvents: 'none',
      userSelect: 'none',
      transform: `rotate(${field.rotation || 0}deg)`
    }}
    draggable={false}
  />
);

const PDFViewer = ({ 
  file, 
  pageNumber, 
  scale, 
  rotation,
  signatureFields, 
  onAddField,
  onFieldDelete,
  onPageChange,
  onScaleChange,
  onRotationChange,
  onDownload,
  onPrint,
  selectedSignature,
  showSidebar = false 
}) => {
  const [numPages, setNumPages] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [hoverPos, setHoverPos] = useState({ x: 0, y: 0 });
  const [showPreview, setShowPreview] = useState(false);
  const [placedField, setPlacedField] = useState(null);
  const containerRef = useRef();

  const handleDocumentLoadSuccess = ({ numPages }) => {
    setNumPages(numPages);
    setLoading(false);
    setError(null);
  };

  const handleDocumentLoadError = (error) => {
    setError(error);
    setLoading(false);
  };

  // Single-click placement, final
  const handlePdfClick = (e) => {
    if (!selectedSignature || placedField) return;
    if (e.target !== containerRef.current) return;
    const rect = containerRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    const newField = {
      id: Date.now(),
      x: x / scale,
      y: y / scale,
      page: pageNumber,
      signature: selectedSignature,
      width: 180,
      height: 60,
      rotation: 0
    };
    onAddField(newField);
    setPlacedField(newField);
    setShowPreview(false);
    toast.success('Signature placed. Click again to place a new one or remove the current.');
  };

  // Track mouse for hover preview
  const handleMouseMove = (e) => {
    if (!selectedSignature) return;
    const rect = containerRef.current.getBoundingClientRect();
    setHoverPos({
      x: e.clientX - rect.left,
      y: e.clientY - rect.top
    });
    setShowPreview(true);
  };
  const handleMouseLeave = () => {
    setShowPreview(false);
  };

  // Remove placed signature
  const handleRemove = () => {
    if (placedField) {
      onFieldDelete(placedField.id);
      setPlacedField(null);
    }
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: { opacity: 1, transition: { duration: 0.3, staggerChildren: 0.1 } }
  };
  if (error) {
    return (
      <motion.div className="flex items-center justify-center h-64 bg-gray-100 rounded-lg" initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
        <div className="text-center">
          <svg className="w-12 h-12 mx-auto text-gray-400 mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
          </svg>
          <p className="text-gray-600">Error loading PDF</p>
          <p className="text-sm text-gray-500">{error.message}</p>
        </div>
      </motion.div>
    );
  }
  return (
    <motion.div className="relative w-full h-full" variants={containerVariants} initial="hidden" animate="visible">
      <PDFToolbar pageNumber={pageNumber} totalPages={numPages || 1} scale={scale} rotation={rotation} onPageChange={onPageChange} onScaleChange={onScaleChange} onRotationChange={onRotationChange} onDownload={onDownload} onPrint={onPrint} />
      <div ref={containerRef} className={`relative flex justify-center focus:outline-none focus:ring-2 focus:ring-indigo-400 transition-all duration-300 ${showSidebar ? 'mr-80' : ''}`} style={{ cursor: selectedSignature ? 'pointer' : 'default', minHeight: 'calc(100vh - 100px)', paddingTop: '80px', paddingBottom: '20px' }} onClick={handlePdfClick} onMouseMove={handleMouseMove} onMouseLeave={handleMouseLeave} aria-label="PDF preview area" aria-live="polite" tabIndex={0}>
        {loading && (<motion.div className="flex items-center justify-center h-64" initial={{ opacity: 0 }} animate={{ opacity: 1 }}><div className="text-center"><div className="animate-spin rounded-full h-8 w-8 border-b-2 border-indigo-600 mx-auto mb-4"></div><p className="text-gray-600">Loading PDF...</p></div></motion.div>)}
        <AnimatePresence mode="wait">{file && (<motion.div key={`${file.name}-${pageNumber}-${scale}-${rotation}`} initial={{ opacity: 0, scale: 0.95 }} animate={{ opacity: 1, scale: 1 }} exit={{ opacity: 0, scale: 0.95 }} transition={{ duration: 0.3 }} className="relative"><Document file={file} onLoadSuccess={handleDocumentLoadSuccess} onLoadError={handleDocumentLoadError} loading={<div className="flex items-center justify-center h-64"><div className="text-center"><div className="animate-spin rounded-full h-8 w-8 border-b-2 border-indigo-600 mx-auto mb-4"></div><p className="text-gray-600">Loading PDF...</p></div></div>}><Page pageNumber={pageNumber} scale={scale} rotate={rotation} className="shadow-lg" /></Document><div className="absolute inset-0 pointer-events-none">
            {signatureFields.filter(field => field.page === pageNumber).map((field) => (
              <motion.div
                key={field.id}
                className="relative"
                initial={{ boxShadow: '0 0 0 0 #6366f1' }}
                animate={placedField && placedField.id === field.id ? { boxShadow: '0 0 0 4px #6366f1' } : { boxShadow: '0 0 0 0 #6366f1' }}
                transition={{ duration: 0.6 }}
                style={{ display: 'inline-block' }}
              >
                <PlacedSignature field={field} scale={scale} />
                {placedField && placedField.id === field.id && (
                  <motion.button
                    className="absolute -top-3 -right-3 bg-red-500 text-white rounded-full w-7 h-7 flex items-center justify-center shadow-lg z-50 focus:outline-none focus:ring-2 focus:ring-red-400"
                    onClick={handleRemove}
                    whileHover={{ scale: 1.1 }}
                    whileTap={{ scale: 0.9 }}
                    aria-label="Remove signature"
                    style={{ pointerEvents: 'auto' }}
                  >
                    <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                    </svg>
                  </motion.button>
                )}
              </motion.div>
            ))}
          </div>{selectedSignature && <SignatureHoverPreview signature={selectedSignature} position={hoverPos} scale={scale} visible={showPreview} />}</motion.div>)}</AnimatePresence>
        {!file && !loading && (<motion.div className="flex items-center justify-center h-64 bg-gray-100 rounded-lg border-2 border-dashed border-gray-300" initial={{ opacity: 0 }} animate={{ opacity: 1 }}><div className="text-center"><svg className="w-12 h-12 mx-auto text-gray-400 mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" /></svg><p className="text-gray-600">No PDF loaded</p><p className="text-sm text-gray-500">Upload a PDF to get started</p></div></motion.div>)}</div>
    </motion.div>
  );
};

export default PDFViewer;
