import React, { useState, useRef, useEffect } from 'react';
import { Document, Page, pdfjs } from 'react-pdf';
import pdfWorker from 'pdfjs-dist/build/pdf.worker.entry';
pdfjs.GlobalWorkerOptions.workerSrc = pdfWorker;

const fallbackPdfUrl = 'https://cdn.syncfusion.com/content/pdf/pdf-succinctly.pdf';

function blobToHex(blob, cb) {
  const reader = new FileReader();
  reader.onload = function(e) {
    const arr = new Uint8Array(e.target.result);
    cb(Array.from(arr).slice(0, 20).map(b => b.toString(16).padStart(2, '0')).join(' '));
  };
  reader.readAsArrayBuffer(blob);
}

function base64ToBlob(base64, type = 'application/pdf') {
  const byteCharacters = atob(base64);
  const byteNumbers = new Array(byteCharacters.length);
  for (let i = 0; i < byteCharacters.length; i++) {
    byteNumbers[i] = byteCharacters.charCodeAt(i);
  }
  const byteArray = new Uint8Array(byteNumbers);
  return new Blob([byteArray], { type });
}

const PDFRenderer = ({ file, base64, onPageChange, initialPage = 1, signatureFields = [], onPageClick, onUpdateField, onRemoveField }) => {
  const [numPages, setNumPages] = useState(null);
  const [pageNumber, setPageNumber] = useState(initialPage);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [pageDims, setPageDims] = useState({ width: 900, height: 1200 });
  const pageContainerRef = useRef();
  const dragFieldRef = useRef(null);
  const dragOffsetRef = useRef({ x: 0, y: 0 });
  const [objectUrl, setObjectUrl] = useState(null);
  const [blobHex, setBlobHex] = useState('');
  const [downloadUrl, setDownloadUrl] = useState(null);

  // Accept base64 for testing
  useEffect(() => {
    if (base64) {
      const blob = base64ToBlob(base64);
      const url = URL.createObjectURL(blob);
      setObjectUrl(url);
      setDownloadUrl(url);
      blobToHex(blob, setBlobHex);
      return () => {
        URL.revokeObjectURL(url);
      };
    }
  }, [base64]);

  // Convert Blob to object URL if needed
  useEffect(() => {
    if (file instanceof Blob) {
      const url = URL.createObjectURL(file);
      setObjectUrl(url);
      setDownloadUrl(url);
      blobToHex(file, setBlobHex);
      return () => {
        URL.revokeObjectURL(url);
      };
    } else if (typeof file === 'string') {
      setObjectUrl(null);
      setDownloadUrl(null);
      setBlobHex('');
    }
  }, [file]);

  // Always use a string for file prop
  const pdfFileToUse = objectUrl || (typeof file === 'string' ? file : fallbackPdfUrl);

  useEffect(() => {
    setPageNumber(initialPage);
  }, [initialPage]);

  const handleLoadSuccess = ({ numPages }) => {
    setNumPages(numPages);
    setLoading(false);
    setError(null);
    if (onPageChange) onPageChange(1);
  };

  const handleLoadError = (err) => {
    setError(err.message || 'Failed to load PDF');
    setLoading(false);
  };

  const handlePageClick = (e) => {
    if (!onPageClick) return;
    const rect = pageContainerRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    onPageClick(pageNumber, x, y, pageDims);
  };

  const handlePageRenderSuccess = (page) => {
    const viewport = page.getViewport({ scale: 1 });
    setPageDims({ width: viewport.width, height: viewport.height });
  };

  const handleDragStart = (e, field) => {
    e.stopPropagation();
    dragFieldRef.current = field.id;
    const rect = pageContainerRef.current.getBoundingClientRect();
    dragOffsetRef.current = {
      x: e.clientX - (rect.left + field.x),
      y: e.clientY - (rect.top + field.y),
    };
    window.addEventListener('mousemove', handleDragMove);
    window.addEventListener('mouseup', handleDragEnd);
  };

  const handleDragMove = (e) => {
    if (!dragFieldRef.current || !onUpdateField) return;
    const rect = pageContainerRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left - dragOffsetRef.current.x;
    const y = e.clientY - rect.top - dragOffsetRef.current.y;
    onUpdateField(dragFieldRef.current, { x, y });
  };

  const handleDragEnd = () => {
    dragFieldRef.current = null;
    window.removeEventListener('mousemove', handleDragMove);
    window.removeEventListener('mouseup', handleDragEnd);
  };

  return (
    <div className="flex flex-col h-full w-full">
      {/* Navigation Toolbar */}
      <div className="pdf-toolbar flex items-center justify-center h-12 px-2 bg-gray-50 border-b border-gray-200 z-10">
        <button
          className="px-3 py-2 bg-gray-200 rounded disabled:opacity-50 font-semibold min-w-[44px] min-h-[36px] focus:outline-none focus:ring-2 focus:ring-indigo-400 mx-1"
          onClick={() => setPageNumber((p) => Math.max(1, p - 1))}
          disabled={pageNumber <= 1}
          aria-label="Previous page"
          title="Previous page"
          tabIndex={0}
        >
          Previous
        </button>
        <span className="text-gray-700 font-medium mx-2" aria-live="polite">
          Page {pageNumber} of {numPages || 1}
        </span>
        <button
          className="px-3 py-2 bg-gray-200 rounded disabled:opacity-50 font-semibold min-w-[44px] min-h-[36px] focus:outline-none focus:ring-2 focus:ring-indigo-400 mx-1"
          onClick={() => setPageNumber((p) => Math.min(numPages || 1, p + 1))}
          disabled={pageNumber >= numPages}
          aria-label="Next page"
          title="Next page"
          tabIndex={0}
        >
          Next
        </button>
      </div>
      {/* PDF Viewer */}
      <div className="flex-1 min-h-0 overflow-auto flex justify-center items-start bg-gray-50">
        <div className="w-full max-w-5xl flex flex-col items-center relative m-auto">
          {/* Debug info */}
          {file instanceof Blob && (
            <div className="text-xs text-gray-500 mb-2">
              <div>Blob size: {file.size} bytes</div>
              <div>Blob type: {file.type}</div>
              <div>First 20 bytes (hex): {blobHex}</div>
              {downloadUrl && (
                <a href={downloadUrl} download="debug.pdf" className="ml-2 px-2 py-1 bg-blue-100 rounded">Download Blob</a>
              )}
            </div>
          )}
          {loading && (
            <div className="flex items-center justify-center h-64">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-indigo-600 mr-3"></div>
              <span>Loading PDF...</span>
              <div className="text-xs text-gray-400 mt-2">{typeof pdfFileToUse === 'string' ? pdfFileToUse : '[Blob] PDF'}</div>
            </div>
          )}
          {error && (
            <div className="text-red-500 p-4 border border-red-200 rounded text-center">
              <p><strong>Error:</strong> {error}</p>
              {downloadUrl && (
                <a href={downloadUrl} download="debug.pdf" className="ml-2 px-2 py-1 bg-blue-100 rounded">Download Blob</a>
              )}
            </div>
          )}
          {!error && !loading && (
            <div
              ref={pageContainerRef}
              className="relative my-8"
              style={{ width: pageDims.width, height: pageDims.height, cursor: onPageClick ? 'crosshair' : 'default' }}
              onClick={handlePageClick}
            >
              <Document
                file={pdfFileToUse}
                onLoadSuccess={handleLoadSuccess}
                onLoadError={handleLoadError}
                error={<div className="text-red-500">Failed to load PDF.</div>}
                loading={null}
              >
                <Page
                  pageNumber={pageNumber}
                  width={900}
                  renderAnnotationLayer={false}
                  renderTextLayer={false}
                  onRenderSuccess={handlePageRenderSuccess}
                />
              </Document>
              {/* Signature overlays for current page only */}
              {signatureFields.filter(f => f.page === pageNumber).map((field, idx) => (
                <div
                  key={field.id || idx}
                  className="absolute border-2 border-indigo-500 bg-white bg-opacity-80 rounded shadow-md flex items-center justify-center cursor-move group"
                  style={{
                    left: field.x - (field.width || 90) / 2,
                    top: field.y - (field.height || 30) / 2,
                    width: field.width || 180,
                    height: field.height || 60,
                    zIndex: 10,
                    pointerEvents: 'auto',
                    userSelect: 'none',
                  }}
                  onMouseDown={e => handleDragStart(e, field)}
                >
                  <span className="text-indigo-700 font-bold select-none">Sign</span>
                  {onRemoveField && (
                    <button
                      className="absolute top-0 right-0 m-1 w-5 h-5 rounded-full bg-red-500 text-white text-xs flex items-center justify-center opacity-80 hover:opacity-100 group-hover:opacity-100"
                      style={{ zIndex: 20 }}
                      onClick={e => { e.stopPropagation(); onRemoveField(field.id); }}
                      aria-label="Remove signature field"
                    >
                      ×
                    </button>
                  )}
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default PDFRenderer;
