import React, { useEffect, useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import { api } from '../context/AuthContext';
import Header from '../components/Header';
import { motion } from 'framer-motion';
import EmptyState from '../components/EmptyState';

const DashboardPage = () => {
  const { user, logout, loading: authLoading, token } = useAuth();
  const navigate = useNavigate();
  const [documents, setDocuments] = useState([]);
  const [page, setPage] = useState(1);
  const [total, setTotal] = useState(0);
  const [limit] = useState(10);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const fetchDocuments = async (page = 1) => {
    setLoading(true);
    setError('');
    try {
      // Always get token from context, fallback to localStorage
      let authToken = token || localStorage.getItem('token');
      console.log('Dashboard fetch token:', authToken);
      const headers = { Authorization: `Bearer ${authToken}` };
      console.log('Dashboard fetch headers:', headers);
      const res = await api.get(`/api/docs?page=${page}&limit=${limit}`, {
        headers,
      });
      setDocuments(res.data.documents);
      setTotal(res.data.pagination.total);
    } catch (err) {
      setError('Failed to load documents.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchDocuments(page);
    // eslint-disable-next-line
  }, [page]);

  const handleLogout = async () => {
    await logout();
    navigate('/login');
  };

  return (
    <>
      <Header />
      <main id="main-content" className="min-h-screen flex items-center justify-center bg-gray-50" role="main" aria-label="Dashboard main content">
        <section className="w-full max-w-3xl p-4 sm:p-8 bg-white rounded-2xl shadow-md" aria-labelledby="dashboard-title">
          <div className="flex flex-col sm:flex-row justify-between items-center mb-4 gap-2">
            <h1 id="dashboard-title" className="text-2xl font-semibold text-indigo-600">Inkswift Dashboard</h1>
            <motion.button
              onClick={handleLogout}
              className="px-4 py-2 bg-rose-500 text-white rounded-xl font-semibold hover:scale-105 transition-transform focus:outline-none focus:ring-2 focus:ring-rose-400 disabled:opacity-50"
              disabled={authLoading}
              aria-label="Logout"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.96 }}
            >
              Logout
            </motion.button>
          </div>
          <div className="mb-2 text-gray-700">Welcome, {user?.firstName} {user?.lastName}!</div>
          <motion.button
            className="mb-4 px-4 py-2 bg-sky-500 text-white rounded-xl font-semibold hover:scale-105 transition-transform focus:outline-none focus:ring-2 focus:ring-sky-400"
            onClick={() => navigate('/upload')}
            aria-label="Upload Document"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.96 }}
          >
            + Upload Document
          </motion.button>
          {error && <div className="text-rose-500 mb-2" role="alert">{error}</div>}
          {loading ? (
            <div className="text-gray-400" aria-live="polite">Loading documents...</div>
          ) : (
            <div>
              {documents.length === 0 ? (
                <EmptyState
                  illustration={
                    <svg viewBox="0 0 64 64" fill="none" className="w-full h-full"><rect x="8" y="16" width="48" height="32" rx="4" fill="#E5E7EB"/><rect x="16" y="24" width="32" height="4" rx="2" fill="#9CA3AF"/><rect x="16" y="32" width="24" height="4" rx="2" fill="#D1D5DB"/></svg>
                  }
                  title="No Documents Yet"
                  description="Upload your first document to get started."
                />
              ) : (
                <ul className="grid grid-cols-1 md:grid-cols-2 gap-4" aria-label="Document list">
                  {documents.map(doc => (
                    <li key={doc._id} className="rounded-2xl shadow-md p-4 bg-white flex flex-col justify-between" aria-label={`Document ${doc.originalName}`}> 
                      <article>
                        <div className="font-semibold text-indigo-600 truncate">{doc.originalName}</div>
                        <div className="text-sm text-gray-500">Uploaded: {new Date(doc.createdAt).toLocaleString()}</div>
                        <div className="text-sm text-gray-700 mt-1">Status: <span className="font-medium">{doc.status}</span></div>
                      </article>
                      <motion.button
                        className="mt-4 px-3 py-1 bg-indigo-600 text-white rounded-xl font-semibold hover:scale-105 transition-transform focus:outline-none focus:ring-2 focus:ring-indigo-400"
                        onClick={() => navigate(`/document/${doc._id}`)}
                        aria-label={`View document ${doc.originalName}`}
                        whileHover={{ scale: 1.05 }}
                        whileTap={{ scale: 0.96 }}
                      >
                        View
                      </motion.button>
                    </li>
                  ))}
                </ul>
              )}
              {/* Pagination */}
              {total > limit && (
                <nav className="flex justify-center mt-6 space-x-2" aria-label="Pagination">
                  <button
                    className="px-3 py-1 rounded bg-gray-200 hover:bg-gray-300 focus:outline-none focus:ring-2 focus:ring-indigo-400"
                    onClick={() => setPage(p => Math.max(1, p - 1))}
                    disabled={page === 1}
                    aria-label="Previous page"
                  >
                    Prev
                  </button>
                  <span className="px-3 py-1" aria-live="polite">Page {page}</span>
                  <button
                    className="px-3 py-1 rounded bg-gray-200 hover:bg-gray-300 focus:outline-none focus:ring-2 focus:ring-indigo-400"
                    onClick={() => setPage(p => p + 1)}
                    disabled={page * limit >= total}
                    aria-label="Next page"
                  >
                    Next
                  </button>
                </nav>
              )}
            </div>
          )}
        </section>
      </main>
    </>
  );
};

export default DashboardPage;
