import * as React from 'react';
import { cva } from 'class-variance-authority';
import { cn } from '../../lib/utils';
import { Loader, LogIn, LogOut } from 'lucide-react';

// Map of string icon names to their corresponding components
const iconMap = {
  loader: Loader,
  'log-in': LogIn,
  'log-out': LogOut,
};

const buttonVariants = cva(
  'inline-flex items-center justify-center rounded-md text-sm font-medium transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:opacity-50 disabled:pointer-events-none ring-offset-background',
  {
    variants: {
      variant: {
        default: 'bg-primary text-primary-foreground hover:bg-primary/90',
        destructive:
          'bg-destructive text-destructive-foreground hover:bg-destructive/90',
        outline:
          'border border-input hover:bg-accent hover:text-accent-foreground',
        secondary:
          'bg-secondary text-secondary-foreground hover:bg-secondary/80',
        ghost: 'hover:bg-accent hover:text-accent-foreground',
        link: 'underline-offset-4 hover:underline text-primary',
      },
      size: {
        default: 'h-10 py-2 px-4',
        sm: 'h-9 px-3 rounded-md',
        lg: 'h-11 px-8 rounded-md',
        icon: 'h-10 w-10',
      },
    },
    defaultVariants: {
      variant: 'default',
      size: 'default',
    },
  }
);

const Button = React.forwardRef(
  (
    {
      className,
      variant,
      size,
      asChild = false,
      isLoading = false,
      loadingText,
      leftIcon: LeftIconProp,
      rightIcon: RightIconProp,
      children,
      ...props
    },
    ref
  ) => {
    // Handle string icon props by looking them up in the iconMap
    const LeftIcon = LeftIconProp && (typeof LeftIconProp === 'string' ? iconMap[LeftIconProp] : LeftIconProp);
    const RightIcon = RightIconProp && (typeof RightIconProp === 'string' ? iconMap[RightIconProp] : RightIconProp);

    // For now, we'll disable the asChild prop to simplify the component
    // and identify if it's the source of the issue
    if (asChild) {
      console.warn('asChild prop is currently disabled in Button component');
    }

    return (
      <button
        className={cn(buttonVariants({ variant, size, className }))}
        ref={ref}
        disabled={isLoading}
        {...props}
      >
        {isLoading ? (
          <>
            <Loader className="animate-spin -ml-1 mr-2 h-4 w-4" />
            {loadingText || 'Loading...'}
          </>
        ) : (
          <>
            {LeftIcon && <LeftIcon className="mr-2 h-4 w-4" />}
            {children}
            {RightIcon && <RightIcon className="ml-2 h-4 w-4" />}
          </>
        )}
      </button>
    );
  }
);

Button.displayName = 'Button';

export { Button, buttonVariants };

// Note: The asChild prop is currently disabled to resolve React child errors.
// We'll reimplement it once we've confirmed the error is fixed.
