import React, { useState, useEffect } from 'react';
import { Document, Page, pdfjs } from 'react-pdf';

// Set up PDF.js worker
pdfjs.GlobalWorkerOptions.workerSrc = `//unpkg.com/pdfjs-dist@${pdfjs.version}/build/pdf.worker.min.js`;

const SimplePDFTest = ({ file }) => {
  const [numPages, setNumPages] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (file) {
      setLoading(true);
      setError(null);
      setNumPages(null);
    }
  }, [file]);

  const handleLoadSuccess = ({ numPages }) => {
    console.log('SimplePDFTest: PDF loaded successfully with', numPages, 'pages');
    setNumPages(numPages);
    setLoading(false);
    setError(null);
  };

  const handleLoadError = (error) => {
    console.error('SimplePDFTest: PDF load error:', error);
    setError(`Failed to load PDF: ${error.message || 'Unknown error'}`);
    setLoading(false);
  };

  if (!file) {
    return <div className="p-4 text-gray-500">No file provided</div>;
  }

  return (
    <div className="border rounded p-4 bg-gray-50">
      <h3 className="font-medium mb-2">Simple PDF Test</h3>
      
      {loading && (
        <div className="text-center p-4">
          <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-blue-600 mx-auto mb-2"></div>
          <p className="text-sm">Loading PDF...</p>
        </div>
      )}

      {error && (
        <div className="text-red-500 p-2 border border-red-200 rounded text-sm">
          <p><strong>Error:</strong> {error}</p>
        </div>
      )}

      {numPages && (
        <div className="text-green-600 p-2 border border-green-200 rounded text-sm">
          <p><strong>Success!</strong> Loaded {numPages} pages.</p>
        </div>
      )}

      <div className="mt-2">
        <Document
          file={file}
          onLoadSuccess={handleLoadSuccess}
          onLoadError={handleLoadError}
          loading={<div className="text-sm">Loading...</div>}
          error={<div className="text-sm text-red-500">Error loading PDF</div>}
        >
          {numPages && (
            <Page 
              pageNumber={1} 
              scale={0.5}
              loading={<div className="text-sm">Loading page...</div>}
              error={<div className="text-sm text-red-500">Error loading page</div>}
            />
          )}
        </Document>
      </div>

      <div className="mt-2 text-xs text-gray-500">
        <div>File type: {typeof file}</div>
        <div>Is Blob: {file instanceof Blob ? 'Yes' : 'No'}</div>
        <div>Size: {file.size || 'Unknown'} bytes</div>
        <div>PDF.js version: {pdfjs.version}</div>
      </div>
    </div>
  );
};

export default SimplePDFTest;
