import React, { useState } from 'react';
import { Document, Page, pdfjs } from 'react-pdf';

// Set up PDF.js worker
pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.min.js`;

const PDFTestComponent = () => {
  const [numPages, setNumPages] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Test with a simple PDF URL
  const testPdfUrl = 'https://mozilla.github.io/pdf.js/web/compressed.tracemonkey-pldi-09.pdf';

  const handleLoadSuccess = ({ numPages }) => {
    console.log('Test PDF loaded successfully with', numPages, 'pages');
    setNumPages(numPages);
    setLoading(false);
    setError(null);
  };

  const handleLoadError = (error) => {
    console.error('Test PDF load error:', error);
    setError(`Failed to load test PDF: ${error.message || 'Unknown error'}`);
    setLoading(false);
  };

  const runTest = () => {
    setLoading(true);
    setError(null);
    setNumPages(null);
  };

  return (
    <div className="p-6 bg-white rounded-lg shadow-md">
      <h2 className="text-xl font-semibold mb-4">PDF.js Test</h2>
      
      <button
        onClick={runTest}
        disabled={loading}
        className="mb-4 px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600 disabled:opacity-50"
      >
        {loading ? 'Testing...' : 'Test PDF.js with Sample PDF'}
      </button>

      {loading && (
        <div className="text-center p-4">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-green-600 mx-auto mb-2"></div>
          <p>Loading test PDF...</p>
        </div>
      )}

      {error && (
        <div className="text-red-500 p-4 border border-red-200 rounded">
          <p><strong>Error:</strong> {error}</p>
        </div>
      )}

      {numPages && (
        <div className="text-green-600 p-4 border border-green-200 rounded">
          <p><strong>Success!</strong> PDF.js is working correctly.</p>
          <p>Loaded PDF with {numPages} pages.</p>
        </div>
      )}

      {numPages && (
        <div className="mt-4 border rounded p-4">
          <h3 className="font-medium mb-2">Test PDF Preview:</h3>
          <Document
            file={testPdfUrl}
            onLoadSuccess={handleLoadSuccess}
            onLoadError={handleLoadError}
            loading={<div>Loading test PDF...</div>}
            error={<div>Error loading test PDF</div>}
          >
            <Page pageNumber={1} scale={0.5} />
          </Document>
        </div>
      )}

      <div className="mt-4 p-3 bg-gray-50 rounded text-sm">
        <h3 className="font-medium mb-2">PDF.js Info:</h3>
        <div>Version: {pdfjs.version}</div>
        <div>Worker Source: {pdfjs.GlobalWorkerOptions.workerSrc}</div>
      </div>
    </div>
  );
};

export default PDFTestComponent;
