import React, { useState, useEffect } from 'react';
import { useNavigate, useSearchParams } from 'react-router-dom';
import axios from 'axios';
import Header from '../components/Header';
import { motion } from 'framer-motion';
import { useAuth } from '../context/AuthContext';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();
  const { login } = useAuth();

  // Check for session expiration or other URL parameters
  useEffect(() => {
    const sessionExpired = searchParams.get('sessionExpired');
    const signedOut = searchParams.get('signedOut');
    
    if (sessionExpired) {
      setError('Your session has expired. Please log in again to continue.');
    } else if (signedOut) {
      setSuccessMessage('You have been successfully signed out.');
    }
  }, [searchParams]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccessMessage('');
    setLoading(true);
    
    try {
      const res = await axios.post('/api/auth/login', { email, password });
      login(res.data.user, res.data.token, res.data.refreshToken);
      
      // Show loading state before navigation
      setSuccessMessage('Login successful! Redirecting...');
      
      // Use a small delay for better UX
      setTimeout(() => {
        navigate('/dashboard');
      }, 1000);
      
    } catch (err) {
      let errorMessage = 'Login failed. Please check your credentials and try again.';
      
      if (err.response) {
        // Handle specific error cases
        if (err.response.status === 401) {
          errorMessage = 'Invalid email or password. Please try again.';
        } else if (err.response.status >= 500) {
          errorMessage = 'Server error. Please try again later.';
        } else if (err.response.data?.message) {
          errorMessage = err.response.data.message;
        }
      } else if (err.request) {
        errorMessage = 'Unable to connect to the server. Please check your internet connection.';
      }
      
      setError(errorMessage);
      setLoading(false);
    }
  };

  return (
    <>
      <Header />
      <main id="main-content" className="min-h-screen flex items-center justify-center bg-gray-50" role="main" aria-label="Login main content">
        <section className="w-full max-w-md p-4 sm:p-8 bg-white rounded-2xl shadow-md" aria-labelledby="login-title">
          <h1 id="login-title" className="text-2xl font-semibold text-indigo-600 mb-4">Inkswift Login</h1>
          
          {/* Success Message */}
          {successMessage && (
            <div className="mb-4 p-3 bg-green-100 text-green-700 rounded-md" role="alert">
              {successMessage}
            </div>
          )}
          
          {/* Error Message */}
          {error && (
            <div className="mb-4 p-3 bg-red-100 text-red-700 rounded-md" role="alert">
              {error}
            </div>
          )}
          
          <form onSubmit={handleSubmit} className="space-y-4" aria-label="Login form">
            <div>
              <label className="block text-sm font-medium text-gray-700" htmlFor="login-email">
                Email
              </label>
              <input
                id="login-email"
                type="email"
                className="mt-1 w-full rounded-lg border-gray-300 focus:ring-2 focus:ring-indigo-600 focus:outline-none p-2 border"
                value={email}
                onChange={e => setEmail(e.target.value)}
                required
                autoFocus
                aria-label="Email address"
                autoComplete="email"
                disabled={loading}
              />
            </div>
            
            <div className="relative">
              <label className="block text-sm font-medium text-gray-700" htmlFor="login-password">
                Password
              </label>
              <div className="relative">
                <input
                  id="login-password"
                  type={showPassword ? 'text' : 'password'}
                  className="mt-1 w-full rounded-lg border-gray-300 focus:ring-2 focus:ring-indigo-600 focus:outline-none p-2 pr-10 border"
                  value={password}
                  onChange={e => setPassword(e.target.value)}
                  required
                  aria-label="Password"
                  autoComplete="current-password"
                  disabled={loading}
                />
                <button
                  type="button"
                  className="absolute right-2 top-1/2 transform -translate-y-1/2 text-sm text-gray-500 hover:text-gray-700 focus:outline-none focus:ring-2 focus:ring-indigo-400 rounded p-1"
                  onClick={() => setShowPassword(v => !v)}
                  tabIndex={0}
                  aria-label={showPassword ? 'Hide password' : 'Show password'}
                  disabled={loading}
                >
                  {showPassword ? 'üôà' : 'üëÅÔ∏è'}
                </button>
              </div>
            </div>
            
            <motion.button
              type="submit"
              className="w-full py-2 px-4 bg-indigo-600 text-white rounded-xl font-semibold hover:scale-105 transition-transform focus:outline-none focus:ring-2 focus:ring-indigo-400 disabled:opacity-50 disabled:cursor-not-allowed"
              disabled={loading}
              aria-label={loading ? 'Logging in...' : 'Login'}
              whileHover={{ scale: loading ? 1 : 1.04 }}
              whileTap={{ scale: loading ? 1 : 0.97 }}
            >
              {loading ? (
                <span className="flex items-center justify-center">
                  <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                  </svg>
                  Logging in...
                </span>
              ) : 'Login'}
            </motion.button>
          </form>
          
          <div className="mt-4 text-sm text-gray-500 text-center">
            Don't have an account?{' '}
            <a 
              href="/register" 
              className="text-sky-500 hover:underline focus:outline-none focus:ring-2 focus:ring-sky-400 rounded px-1" 
              aria-label="Go to Register page"
              tabIndex={loading ? -1 : 0}
            >
              Register
            </a>
          </div>
          
          <div className="mt-2 text-xs text-center">
            <a 
              href="/forgot-password" 
              className="text-indigo-500 hover:underline focus:outline-none focus:ring-2 focus:ring-indigo-400 rounded px-1" 
              aria-label="Forgot Password"
              tabIndex={loading ? -1 : 0}
            >
              Forgot Password?
            </a>
          </div>
        </section>
      </main>
    </>
  );
};

export default LoginPage;
