import React, { useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../components/Header';
import { motion } from 'framer-motion';
import { useAuth, api } from '../context/AuthContext';

const UploadPage = () => {
  const [file, setFile] = useState(null);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);
  const [loading, setLoading] = useState(false);
  const fileInputRef = useRef();
  const navigate = useNavigate();
  const { token } = useAuth();

  const handleDrop = (e) => {
    e.preventDefault();
    setError('');
    const dropped = e.dataTransfer.files[0];
    if (dropped) validateAndSetFile(dropped);
  };

  const handleFileChange = (e) => {
    setError('');
    const selected = e.target.files[0];
    if (selected) validateAndSetFile(selected);
  };

  const validateAndSetFile = (f) => {
    if (f.type !== 'application/pdf') {
      setError('Only PDF files are allowed.');
      return;
    }
    if (f.size > 10 * 1024 * 1024) {
      setError('File size must be 10MB or less.');
      return;
    }
    setFile(f);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file) return setError('Please select a PDF file.');
    setLoading(true);
    setError('');
    setSuccess(false);
    try {
      const formData = new FormData();
      formData.append('file', file);
      let authToken = token || localStorage.getItem('token');
      await api.post('/api/docs/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${authToken}`,
        },
      });
      setSuccess(true);
      setFile(null);
    } catch (err) {
      setError(err.response?.data?.message || 'Upload failed.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <Header />
      <main id="main-content" className="min-h-screen flex items-center justify-center bg-gray-50" role="main" aria-label="Upload main content">
        <section className="w-full max-w-md p-4 sm:p-8 bg-white rounded-2xl shadow-md" aria-labelledby="upload-title">
          <h1 id="upload-title" className="text-2xl font-semibold text-indigo-600 mb-4">Upload PDF Document</h1>
          <form onSubmit={handleUpload} aria-label="Upload PDF form">
            <div
              className="flex flex-col items-center justify-center border-2 border-dashed border-sky-500 rounded-xl p-6 mb-4 cursor-pointer bg-gray-50 hover:bg-sky-50 transition focus-within:ring-2 focus-within:ring-sky-400"
              onDrop={handleDrop}
              onDragOver={e => e.preventDefault()}
              onClick={() => fileInputRef.current.click()}
              tabIndex={0}
              role="button"
              aria-label="Drag and drop a PDF here, or click to select"
              onKeyDown={e => { if (e.key === 'Enter' || e.key === ' ') fileInputRef.current.click(); }}
            >
              <input
                type="file"
                accept="application/pdf"
                className="hidden"
                ref={fileInputRef}
                onChange={handleFileChange}
                aria-label="Select PDF file"
              />
              <span className="text-sky-500 font-semibold">Drag & drop a PDF here, or click to select</span>
              {file && <span className="mt-2 text-gray-700">Selected: {file.name}</span>}
            </div>
            {error && <div className="text-rose-500 text-sm mb-2" role="alert">{error}</div>}
            {success && (
              <div className="text-emerald-500 text-sm mb-2">Upload successful! <motion.button type="button" className="underline text-sky-500 focus:outline-none focus:ring-2 focus:ring-sky-400" onClick={() => navigate('/dashboard')} whileHover={{ scale: 1.08 }} whileTap={{ scale: 0.96 }}>Go to Dashboard</motion.button></div>
            )}
            <motion.button
              type="submit"
              className="w-full py-2 px-4 bg-indigo-600 text-white rounded-xl font-semibold hover:scale-105 transition-transform focus:outline-none focus:ring-2 focus:ring-indigo-400 disabled:opacity-50"
              disabled={loading}
              aria-label="Upload PDF"
              whileHover={{ scale: 1.04 }}
              whileTap={{ scale: 0.97 }}
            >
              {loading ? 'Uploading...' : 'Upload PDF'}
            </motion.button>
          </form>
        </section>
      </main>
    </>
  );
};

export default UploadPage;
