import React, { useEffect, useRef, useState } from 'react';
import { Document, Page, pdfjs } from 'react-pdf';
pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.min.js`;

export default function PDFStandaloneTest() {
  const containerRef = useRef();
  const [canvasFound, setCanvasFound] = useState(null);

  useEffect(() => {
    if (containerRef.current) {
      const canvas = containerRef.current.querySelector('canvas');
      if (canvas) {
        setCanvasFound(true);
        console.log('[PDFStandaloneTest] Canvas found:', canvas);
      } else {
        setCanvasFound(false);
        console.warn('[PDFStandaloneTest] No canvas found in PDFStandaloneTest');
      }
    }
  });

  return (
    <div ref={containerRef} style={{ background: '#fffbe6', border: '4px solid orange', margin: 24, padding: 24 }}>
      <h2 style={{ color: '#b45309', fontWeight: 'bold', marginBottom: 16 }}>Standalone PDF Test (Local /test.pdf)</h2>
      <Document file="/test.pdf">
        <Page pageNumber={1} width={900} />
      </Document>
      {canvasFound === false && (
        <div style={{ color: 'red', fontWeight: 'bold', marginTop: 16 }}>
          No &lt;canvas&gt; found. PDF is not rendering.<br />
          Check the console and network tab for errors.
        </div>
      )}
      {canvasFound === true && (
        <div style={{ color: 'green', fontWeight: 'bold', marginTop: 16 }}>
          Canvas found! PDF should be visible above.
        </div>
      )}
    </div>
  );
}
