import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const Header = () => {
  const { user, logout, loading } = useAuth();
  const navigate = useNavigate();

  const handleLogout = async () => {
    await logout();
    navigate('/login');
  };

  return (
    <header className="w-full bg-white shadow-md sticky top-0 z-50" role="banner">
      <a href="#main-content" className="sr-only focus:not-sr-only focus:absolute focus:top-2 focus:left-2 bg-indigo-600 text-white px-3 py-1 rounded">Skip to main content</a>
      <nav className="max-w-5xl mx-auto flex items-center justify-between px-4 py-3" aria-label="Main navigation">
        <Link to={user ? "/dashboard" : "/"} className="flex items-center gap-2" aria-label="Inkswift Home">
          <img src="/inkswift-logo.svg" alt="Inkswift Logo" className="h-8 w-8" />
          <span className="font-bold text-indigo-700 text-lg">Inkswift</span>
        </Link>
        <div className="flex items-center gap-4">
          {user ? (
            <>
              <Link to="/dashboard" className="text-gray-700 hover:text-indigo-600 focus:outline-none focus:ring-2 focus:ring-indigo-500 rounded px-2 py-1 transition">Dashboard</Link>
              <Link to="/upload" className="text-gray-700 hover:text-indigo-600 focus:outline-none focus:ring-2 focus:ring-indigo-500 rounded px-2 py-1 transition">Upload</Link>
              <button
                onClick={handleLogout}
                className="text-rose-600 hover:text-white hover:bg-rose-600 focus:outline-none focus:ring-2 focus:ring-rose-500 rounded px-3 py-1 font-semibold transition disabled:opacity-50"
                disabled={loading}
                aria-label="Logout"
              >Logout</button>
            </>
          ) : (
            <>
              <Link to="/login" className="text-gray-700 hover:text-indigo-600 focus:outline-none focus:ring-2 focus:ring-indigo-500 rounded px-2 py-1 transition">Login</Link>
              <Link to="/register" className="text-gray-700 hover:text-indigo-600 focus:outline-none focus:ring-2 focus:ring-indigo-500 rounded px-2 py-1 transition">Register</Link>
            </>
          )}
        </div>
      </nav>
    </header>
  );
};

export default Header;
