import React, { useState } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { motion, AnimatePresence } from 'framer-motion';

const Header = () => {
  const { user, logout, loading } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();
  const [showLogoutConfirm, setShowLogoutConfirm] = useState(false);
  const [isLoggingOut, setIsLoggingOut] = useState(false);

  const handleLogout = async () => {
    setIsLoggingOut(true);
    try {
      await logout();
      // Add signedOut parameter to show success message
      navigate('/login?signedOut=true', { replace: true });
    } catch (error) {
      console.error('Logout error:', error);
      // If logout fails, still redirect but show an error message
      navigate('/login?error=logout_failed', { replace: true });
    } finally {
      setIsLoggingOut(false);
      setShowLogoutConfirm(false);
    }
  };

  // Don't show header on login/signup pages
  if (['/login', '/register', '/forgot-password', '/reset-password'].includes(location.pathname)) {
    return null;
  }

  return (
    <header className="w-full bg-white shadow-md sticky top-0 z-50" role="banner">
      <a href="#main-content" className="sr-only focus:not-sr-only focus:absolute focus:top-2 focus:left-2 bg-indigo-600 text-white px-3 py-1 rounded">
        Skip to main content
      </a>
      
      <nav className="max-w-7xl mx-auto flex items-center justify-between px-4 sm:px-6 lg:px-8 py-3" aria-label="Main navigation">
        <Link 
          to={user ? "/dashboard" : "/"} 
          className="flex items-center gap-2 focus:outline-none focus:ring-2 focus:ring-indigo-500 rounded" 
          aria-label="Inkswift Home"
        >
          <img src="/inkswift-logo.svg" alt="Inkswift Logo" className="h-8 w-8" />
          <span className="font-bold text-indigo-700 text-lg">Inkswift</span>
        </Link>
        
        <div className="flex items-center gap-4">
          {user ? (
            <>
              <Link 
                to="/dashboard" 
                className="text-gray-700 hover:text-indigo-600 focus:outline-none focus:ring-2 focus:ring-indigo-500 rounded px-3 py-2 transition font-medium"
                aria-current={location.pathname === '/dashboard' ? 'page' : undefined}
              >
                Dashboard
              </Link>
              <Link 
                to="/upload" 
                className="text-gray-700 hover:text-indigo-600 focus:outline-none focus:ring-2 focus:ring-indigo-500 rounded px-3 py-2 transition font-medium"
                aria-current={location.pathname === '/upload' ? 'page' : undefined}
              >
                Upload
              </Link>
              
              <div className="relative">
                <button
                  onClick={() => setShowLogoutConfirm(true)}
                  className="text-rose-600 hover:text-white hover:bg-rose-600 focus:outline-none focus:ring-2 focus:ring-rose-500 rounded px-3 py-2 font-medium transition flex items-center gap-1 disabled:opacity-50"
                  disabled={loading || isLoggingOut}
                  aria-label="Logout"
                >
                  {isLoggingOut ? (
                    <>
                      <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-rose-600" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                        <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                        <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                      </svg>
                      Logging out...
                    </>
                  ) : 'Logout'}
                </button>
                
                {/* Logout Confirmation Modal */}
                <AnimatePresence>
                  {showLogoutConfirm && (
                    <motion.div 
                      className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50"
                      initial={{ opacity: 0 }}
                      animate={{ opacity: 1 }}
                      exit={{ opacity: 0 }}
                      onClick={() => setShowLogoutConfirm(false)}
                    >
                      <motion.div 
                        className="bg-white rounded-lg p-6 max-w-sm w-full mx-4 shadow-xl"
                        initial={{ scale: 0.9, opacity: 0 }}
                        animate={{ scale: 1, opacity: 1 }}
                        exit={{ scale: 0.9, opacity: 0 }}
                        onClick={e => e.stopPropagation()}
                      >
                        <h3 className="text-lg font-medium text-gray-900 mb-4">Are you sure you want to log out?</h3>
                        <div className="flex justify-end gap-3">
                          <button
                            onClick={() => setShowLogoutConfirm(false)}
                            className="px-4 py-2 text-sm font-medium text-gray-700 hover:bg-gray-100 rounded-md focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                            disabled={isLoggingOut}
                          >
                            Cancel
                          </button>
                          <button
                            onClick={handleLogout}
                            className="px-4 py-2 text-sm font-medium text-white bg-rose-600 hover:bg-rose-700 rounded-md focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-rose-500 disabled:opacity-50"
                            disabled={isLoggingOut}
                          >
                            {isLoggingOut ? 'Logging out...' : 'Logout'}
                          </button>
                        </div>
                      </motion.div>
                    </motion.div>
                  )}
                </AnimatePresence>
              </div>
            </>
          ) : (
            <>
              <Link 
                to="/login" 
                className="text-gray-700 hover:text-indigo-600 focus:outline-none focus:ring-2 focus:ring-indigo-500 rounded px-3 py-2 transition font-medium"
                state={{ from: location }}
              >
                Login
              </Link>
              <Link 
                to="/register" 
                className="bg-indigo-600 text-white hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 rounded-md px-4 py-2 transition font-medium"
              >
                Sign up
              </Link>
            </>
          )}
        </div>
      </nav>
    </header>
  );
};

export default Header;
