import React, { useState, useRef } from 'react';
import { Document, Page, pdfjs } from 'react-pdf';
import PropTypes from 'prop-types';

// Standardize PDF.js worker source
pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.min.js`;

/**
 * PDFRenderer - Clean, robust PDF viewer for the Document Signature App
 * Props:
 *   file: Blob | string (PDF file or URL)
 *   onPageChange: function (optional)
 *   initialPage: number (optional)
 *   signatureFields: array of signature field objects (optional)
 *   onPageClick: function (optional)
 *   onUpdateField: function (optional)
 *   onRemoveField: function (optional)
 */
const PDFRenderer = ({ file, onPageChange, initialPage = 1, signatureFields = [], onPageClick, onUpdateField, onRemoveField }) => {
  const [numPages, setNumPages] = useState(null);
  const [pageNumber, setPageNumber] = useState(initialPage);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [pageDims, setPageDims] = useState({ width: 900, height: 1200 });
  const pageContainerRef = useRef();
  const dragFieldRef = useRef(null);
  const dragOffsetRef = useRef({ x: 0, y: 0 });

  const handleLoadSuccess = ({ numPages }) => {
    setNumPages(numPages);
    setLoading(false);
    setError(null);
    if (onPageChange) onPageChange(1);
  };

  const handleLoadError = (err) => {
    setError(err.message || 'Failed to load PDF');
    setLoading(false);
  };

  const handlePageChange = (offset) => {
    const newPage = Math.min(Math.max(pageNumber + offset, 1), numPages);
    setPageNumber(newPage);
    if (onPageChange) onPageChange(newPage);
  };

  // Get click coordinates relative to PDF page
  const handlePageClick = (e) => {
    if (!onPageClick) return;
    const rect = pageContainerRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    onPageClick(pageNumber, x, y, pageDims);
  };

  // When the page renders, get its dimensions
  const handlePageRenderSuccess = (page) => {
    const viewport = page.getViewport({ scale: 1 });
    setPageDims({ width: viewport.width, height: viewport.height });
  };

  // Drag logic for signature fields
  const handleDragStart = (e, field) => {
    e.stopPropagation();
    dragFieldRef.current = field.id;
    const rect = pageContainerRef.current.getBoundingClientRect();
    dragOffsetRef.current = {
      x: e.clientX - (rect.left + field.x),
      y: e.clientY - (rect.top + field.y),
    };
    window.addEventListener('mousemove', handleDragMove);
    window.addEventListener('mouseup', handleDragEnd);
  };

  const handleDragMove = (e) => {
    if (!dragFieldRef.current || !onUpdateField) return;
    const rect = pageContainerRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left - dragOffsetRef.current.x;
    const y = e.clientY - rect.top - dragOffsetRef.current.y;
    onUpdateField(dragFieldRef.current, { x, y });
  };

  const handleDragEnd = () => {
    dragFieldRef.current = null;
    window.removeEventListener('mousemove', handleDragMove);
    window.removeEventListener('mouseup', handleDragEnd);
  };

  return (
    <div className="w-full h-full flex flex-col items-center justify-center bg-gray-50 relative">
      {loading && (
        <div className="flex items-center justify-center h-64">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-indigo-600 mr-3"></div>
          <span>Loading PDF...</span>
        </div>
      )}
      {error && (
        <div className="text-red-500 p-4 border border-red-200 rounded text-center">
          <p><strong>Error:</strong> {error}</p>
        </div>
      )}
      {!error && (
        <>
          <div className="relative flex flex-col items-center w-full max-w-5xl">
            <div
              ref={pageContainerRef}
              className="relative mb-16"
              style={{ width: pageDims.width, height: pageDims.height, cursor: onPageClick ? 'crosshair' : 'default' }}
              onClick={handlePageClick}
            >
              <Document
                file={file}
                onLoadSuccess={handleLoadSuccess}
                onLoadError={handleLoadError}
                loading={null}
                error={null}
              >
                <Page
                  pageNumber={pageNumber}
                  width={900}
                  renderAnnotationLayer={false}
                  renderTextLayer={false}
                  onRenderSuccess={handlePageRenderSuccess}
                />
              </Document>
              {/* Signature overlays */}
              {signatureFields.filter(f => f.page === pageNumber).map((field, idx) => (
                <div
                  key={field.id || idx}
                  className="absolute border-2 border-indigo-500 bg-white bg-opacity-80 rounded shadow-md flex items-center justify-center cursor-move group"
                  style={{
                    left: field.x - (field.width || 90) / 2,
                    top: field.y - (field.height || 30) / 2,
                    width: field.width || 180,
                    height: field.height || 60,
                    zIndex: 10,
                    pointerEvents: 'auto',
                    userSelect: 'none',
                  }}
                  onMouseDown={e => handleDragStart(e, field)}
                >
                  <span className="text-indigo-700 font-bold select-none">Sign</span>
                  {onRemoveField && (
                    <button
                      className="absolute top-0 right-0 m-1 w-5 h-5 rounded-full bg-red-500 text-white text-xs flex items-center justify-center opacity-80 hover:opacity-100 group-hover:opacity-100"
                      style={{ zIndex: 20 }}
                      onClick={e => { e.stopPropagation(); onRemoveField(field.id); }}
                      aria-label="Remove signature field"
                    >
                      Ã—
                    </button>
                  )}
                </div>
              ))}
            </div>
          </div>
          {/* Pagination Controls - always visible below the PDF page */}
          {numPages && numPages > 1 && (
            <div className="flex items-center justify-center mt-4 space-x-4 bg-white rounded shadow p-2" style={{ opacity: 1 }}>
              <button
                className="px-4 py-3 bg-gray-200 rounded disabled:opacity-50 font-semibold min-w-[44px] min-h-[44px] focus:outline-none focus:ring-2 focus:ring-indigo-400"
                onClick={() => handlePageChange(-1)}
                disabled={pageNumber <= 1}
                aria-label="Previous page"
                title="Previous page"
                tabIndex={0}
              >
                Previous
              </button>
              <span className="text-gray-700 font-medium" aria-live="polite">
                Page {pageNumber} of {numPages}
              </span>
              <button
                className="px-4 py-3 bg-gray-200 rounded disabled:opacity-50 font-semibold min-w-[44px] min-h-[44px] focus:outline-none focus:ring-2 focus:ring-indigo-400"
                onClick={() => handlePageChange(1)}
                disabled={pageNumber >= numPages}
                aria-label="Next page"
                title="Next page"
                tabIndex={0}
              >
                Next
              </button>
            </div>
          )}
        </>
      )}
    </div>
  );
};

PDFRenderer.propTypes = {
  file: PropTypes.oneOfType([
    PropTypes.instanceOf(Blob),
    PropTypes.string,
  ]).isRequired,
  onPageChange: PropTypes.func,
  initialPage: PropTypes.number,
  signatureFields: PropTypes.array,
  onPageClick: PropTypes.func,
  onUpdateField: PropTypes.func,
  onRemoveField: PropTypes.func,
};

export default PDFRenderer;
