import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useAuth, api } from '../context/AuthContext';
import PDFRenderer from '../components/PDFRenderer';
import Header from '../components/Header';

const DEFAULT_SIG_WIDTH = 180;
const DEFAULT_SIG_HEIGHT = 60;

// TODO: Integrate signature overlay and placement logic

const DocumentViewerPage = () => {
  const { id } = useParams();
  const { token } = useAuth();
  const [pdfFile, setPdfFile] = useState(null); // Blob or URL
  const [docInfo, setDocInfo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [signatureFields, setSignatureFields] = useState([]);

  // Fetch document metadata and PDF blob from backend
  useEffect(() => {
    if (!id) return;
    setLoading(true);
    setError('');
    setPdfFile(null);
    setDocInfo(null);
    const fetchDoc = async () => {
      try {
        // Fetch document metadata
        const docRes = await api.get(`/api/docs/${id}`);
        setDocInfo(docRes.data.document);
        // Fetch PDF blob
        const pdfRes = await api.get(`/api/docs/${id}/download`, {
          responseType: 'blob',
        });
        setPdfFile(new Blob([pdfRes.data], { type: 'application/pdf' }));
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to load document.');
      } finally {
        setLoading(false);
      }
    };
    fetchDoc();
  }, [id, token]);

  // File upload for local testing (fallback)
  const handleFileChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      setPdfFile(e.target.files[0]);
      setDocInfo(null);
      setError('');
    }
  };

  // Handle click-to-place signature field
  const handlePageClick = (page, x, y, pageDims) => {
    // Center the signature field at the click
    const newField = {
      id: Date.now() + Math.random(),
      page,
      x,
      y,
      width: DEFAULT_SIG_WIDTH,
      height: DEFAULT_SIG_HEIGHT,
    };
    setSignatureFields(fields => [...fields, newField]);
  };

  // Update a signature field's position
  const handleUpdateField = (id, updates) => {
    setSignatureFields(fields => fields.map(f => f.id === id ? { ...f, ...updates } : f));
  };

  // Remove a signature field
  const handleRemoveField = (id) => {
    setSignatureFields(fields => fields.filter(f => f.id !== id));
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col">
      <Header />
      <main className="flex-1 flex flex-col items-center justify-center p-4">
        <div className="w-full max-w-5xl mx-auto flex flex-col items-center">
          <h1 className="text-2xl font-bold mb-4">Document Viewer</h1>
          {/* File upload for testing (remove in production) */}
          <input type="file" accept="application/pdf" onChange={handleFileChange} className="mb-4" />
          {loading && <div className="text-gray-500">Loading document...</div>}
          {error && <div className="text-red-500">{error}</div>}
          {pdfFile && !loading && !error && (
            <PDFRenderer
              file={pdfFile}
              signatureFields={signatureFields}
              onPageClick={handlePageClick}
              onUpdateField={handleUpdateField}
              onRemoveField={handleRemoveField}
            />
          )}
          {!pdfFile && !loading && !error && (
            <div className="text-gray-500">Upload a PDF to preview</div>
          )}
        </div>
      </main>
    </div>
  );
};

export default DocumentViewerPage;
